import React, { useState, useRef, useEffect } from 'react';
import ReactQuill from 'react-quill';
import axios from 'axios';
import 'react-quill/dist/quill.snow.css';
import './AdminPanel.css';

const AdminPanel = () => {
  const [activeTab, setActiveTab] = useState('users');

  return (
    <div className="admin-panel">
      <section className="hero">
        <h1>Панель администратора</h1>
        <p>Управление пользователями и контентом</p>
      </section>

      <div className="admin-tabs">
        <button 
          className={`tab-button ${activeTab === 'users' ? 'active' : ''}`}
          onClick={() => setActiveTab('users')}
        >
          Пользователи
        </button>
        <button 
          className={`tab-button ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          Новости
        </button>
      </div>

      <div className="tab-content">
        {activeTab === 'users' ? <UserManagement /> : <NewsManagement />}
      </div>
    </div>
  );
};

// Компонент управления пользователями
const UserManagement = () => {
  const [users, setUsers] = useState([
    { id: 1, name: 'Администратор', email: 'admin@nsrz.ru', role: 'admin' },
    { id: 2, name: 'Сотрудник', email: 'user@nsrz.ru', role: 'employee' }
  ]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newUser, setNewUser] = useState({ name: '', email: '', password: '', role: 'employee' });
  const [editingUserId, setEditingUserId] = useState(null);
  const [editUserData, setEditUserData] = useState({ name: '', email: '', role: 'employee' });

  // Добавление пользователя
  const handleAddUser = (e) => {
    e.preventDefault();
    const newUserId = users.length > 0 ? Math.max(...users.map(u => u.id)) + 1 : 1;
    setUsers([...users, { ...newUser, id: newUserId }]);
    setNewUser({ name: '', email: '', password: '', role: 'employee' });
    setShowAddForm(false);
  };

  // Удаление пользователя
  const handleDeleteUser = (userId) => {
    if (window.confirm(`Вы уверены, что хотите удалить пользователя ${users.find(u => u.id === userId)?.name}?`)) {
      setUsers(users.filter(user => user.id !== userId));
    }
  };

  // Начало редактирования
  const handleStartEdit = (user) => {
    setEditingUserId(user.id);
    setEditUserData({
      name: user.name,
      email: user.email,
      role: user.role
    });
  };

  // Сохранение изменений
  const handleSaveEdit = () => {
    setUsers(users.map(user => 
      user.id === editingUserId ? { ...user, ...editUserData } : user
    ));
    setEditingUserId(null);
  };

  // Отмена редактирования
  const handleCancelEdit = () => {
    setEditingUserId(null);
  };

  return (
    <div className="user-management">
      <div className="section-header">
        <h2>Управление пользователями</h2>
        <button 
          className="add-button" 
          onClick={() => {
            setEditingUserId(null);
            setShowAddForm(true);
          }}
        >
          Добавить пользователя
        </button>
      </div>

      {showAddForm && (
        <div className="add-form-container">
          <form onSubmit={handleAddUser} className="add-form">
            <h3>Добавить нового пользователя</h3>
            <div className="form-group">
              <label>ФИО</label>
              <input
                type="text"
                value={newUser.name}
                onChange={(e) => setNewUser({...newUser, name: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                value={newUser.email}
                onChange={(e) => setNewUser({...newUser, email: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Пароль</label>
              <input
                type="password"
                value={newUser.password}
                onChange={(e) => setNewUser({...newUser, password: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Роль</label>
              <select
                value={newUser.role}
                onChange={(e) => setNewUser({...newUser, role: e.target.value})}
              >
                <option value="employee">Сотрудник</option>
                <option value="admin">Администратор</option>
              </select>
            </div>
            <div className="form-buttons">
              <button type="submit" className="submit-button">Сохранить</button>
              <button 
                type="button" 
                onClick={() => setShowAddForm(false)} 
                className="cancel-button"
              >
                Отмена
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="users-list">
        {users.map(user => (
          <div key={user.id} className="user-card">
            <div className="user-info">
              {editingUserId === user.id ? (
                <>
                  <div className="form-group">
                    <label>ФИО</label>
                    <input
                      type="text"
                      value={editUserData.name}
                      onChange={(e) => setEditUserData({...editUserData, name: e.target.value})}
                      className="edit-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Email</label>
                    <input
                      type="email"
                      value={editUserData.email}
                      onChange={(e) => setEditUserData({...editUserData, email: e.target.value})}
                      className="edit-input"
                      required
                    />
                  </div>
                  <div className="form-group">
                    <label>Роль</label>
                    <select
                      value={editUserData.role}
                      onChange={(e) => setEditUserData({...editUserData, role: e.target.value})}
                      className="edit-select"
                    >
                      <option value="employee">Сотрудник</option>
                      <option value="admin">Администратор</option>
                    </select>
                  </div>
                </>
              ) : (
                <>
                  <h3>{user.name}</h3>
                  <p>{user.email}</p>
                  <span className={`role-badge ${user.role}`}>
                    {user.role === 'admin' ? 'Администратор' : 'Сотрудник'}
                  </span>
                </>
              )}
            </div>
            <div className="user-actions">
              {editingUserId === user.id ? (
                <>
                  <button 
                    className="save-button"
                    onClick={handleSaveEdit}
                  >
                    Сохранить
                  </button>
                  <button 
                    className="cancel-button"
                    onClick={handleCancelEdit}
                  >
                    Отмена
                  </button>
                </>
              ) : (
                <>
                  <button 
                    className="edit-button"
                    onClick={() => handleStartEdit(user)}
                    disabled={showAddForm}
                  >
                    Редактировать
                  </button>
                  <button 
                    className="delete-button"
                    onClick={() => handleDeleteUser(user.id)}
                    disabled={showAddForm}
                  >
                    Удалить
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Компонент управления новостями
const NewsManagement = () => {
  const [news, setNews] = useState([
    { 
      id: 1, 
      title: 'Обновление системы', 
      content: '<p>Запущена новая версия портала</p>',
      date: '2024-03-20',
      author: 'Администратор'
    }
  ]);
  const [showAddForm, setShowAddForm] = useState(false);
  const [newNews, setNewNews] = useState({ 
    title: '', 
    content: '' 
  });
  const [editingNewsId, setEditingNewsId] = useState(null);
  const [editNewsData, setEditNewsData] = useState({ 
    title: '', 
    content: '' 
  });
  const quillRef = useRef(null);
  const editQuillRef = useRef(null);

  // Безопасный обработчик изменений для Quill
  const handleContentChange = (content, delta, source, editor) => {
    setNewNews(prev => ({
      ...prev,
      content: editor.getHTML()
    }));
  };

  // Обработчик изменений для редактирования
  const handleEditContentChange = (content, delta, source, editor) => {
    setEditNewsData(prev => ({
      ...prev,
      content: editor.getHTML()
    }));
  };

  // Добавление новости
  const handleAddNews = (e) => {
    e.preventDefault();
    const currentDate = new Date().toISOString().split('T')[0];
    setNews([...news, { 
      ...newNews, 
      id: news.length + 1,
      date: currentDate,
      author: 'Администратор'
    }]);
    setNewNews({ title: '', content: '' });
    setShowAddForm(false);
  };

  // Удаление новости
  const handleDeleteNews = (newsId) => {
    if (window.confirm('Вы уверены, что хотите удалить эту новость?')) {
      setNews(news.filter(item => item.id !== newsId));
    }
  };

  // Начало редактирования
  const handleStartEdit = (newsItem) => {
    setEditingNewsId(newsItem.id);
    setEditNewsData({
      title: newsItem.title,
      content: newsItem.content
    });
  };

  // Сохранение изменений
  const handleSaveEdit = (newsId) => {
    setNews(news.map(item => 
      item.id === newsId ? { 
        ...item, 
        title: editNewsData.title,
        content: editNewsData.content,
        date: new Date().toISOString().split('T')[0] // Обновляем дату
      } : item
    ));
    setEditingNewsId(null);
  };

  // Отмена редактирования
  const handleCancelEdit = () => {
    setEditingNewsId(null);
  };

  // Конфигурация Quill
  const modules = {
    toolbar: [
      [{ 'header': [1, 2, 3, false] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'color': [] }, { 'background': [] }],
      [{ 'align': [] }],
      ['link', 'image'],
      ['clean']
    ],
    clipboard: {
      matchVisual: false,
    }
  };

  const formats = [
    'header',
    'bold', 'italic', 'underline', 'strike',
    'color', 'background',
    'align',
    'link', 'image'
  ];

  return (
    <div className="news-management">
      <div className="section-header">
        <h2>Управление новостями</h2>
        <button className="add-button" onClick={() => setShowAddForm(true)}>
          Добавить новость
        </button>
      </div>

      {showAddForm && (
        <div className="add-form-container">
          <form onSubmit={handleAddNews} className="add-form">
            <h3>Добавить новость</h3>
            <div className="form-group">
              <label>Заголовок</label>
              <input
                type="text"
                value={newNews.title}
                onChange={(e) => setNewNews({...newNews, title: e.target.value})}
                required
              />
            </div>
            <div className="form-group">
              <label>Содержание</label>
              <ReactQuill
                ref={quillRef}
                value={newNews.content}
                onChange={handleContentChange}
                modules={modules}
                formats={formats}
                theme="snow"
                placeholder="Введите текст новости..."
                style={{ minHeight: '200px', backgroundColor: 'white' }}
              />
            </div>
            <div className="form-buttons">
              <button type="submit" className="submit-button">Сохранить</button>
              <button 
                type="button" 
                onClick={() => setShowAddForm(false)} 
                className="cancel-button"
              >
                Отмена
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="news-list">
        {news.map(item => (
          <div key={item.id} className="news-card">
            <div className="news-info">
              {editingNewsId === item.id ? (
                <>
                  <input
                    type="text"
                    value={editNewsData.title}
                    onChange={(e) => setEditNewsData({...editNewsData, title: e.target.value})}
                    className="edit-input"
                  />
                  <ReactQuill
                    ref={editQuillRef}
                    value={editNewsData.content}
                    onChange={handleEditContentChange}
                    modules={modules}
                    formats={formats}
                    theme="snow"
                    style={{ minHeight: '200px', backgroundColor: 'white' }}
                  />
                </>
              ) : (
                <>
                  <h3>{item.title}</h3>
                  <div dangerouslySetInnerHTML={{ __html: item.content }} />
                  <div className="news-meta">
                    <span>Обновлено: {item.date}</span>
                    <span>{item.author}</span>
                  </div>
                </>
              )}
            </div>
            <div className="news-actions">
              {editingNewsId === item.id ? (
                <>
                  <button 
                    className="save-button"
                    onClick={() => handleSaveEdit(item.id)}
                  >
                    Сохранить
                  </button>
                  <button 
                    className="cancel-button"
                    onClick={handleCancelEdit}
                  >
                    Отмена
                  </button>
                </>
              ) : (
                <>
                  <button 
                    className="edit-button"
                    onClick={() => handleStartEdit(item)}
                  >
                    Редактировать
                  </button>
                  <button 
                    className="delete-button"
                    onClick={() => handleDeleteNews(item.id)}
                  >
                    Удалить
                  </button>
                </>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

const NewsEditor = ({ initialData, onSave, onCancel }) => {
  // Вынести логику редактора сюда
};

export default AdminPanel;
