import React, { useState, useEffect } from 'react';
import { createUser, updateUser } from '../api/usersApi';
import { getDepartments } from '../api/departmentsApi';
import './UsersEditor.css';

/**
 * Editor for creating / updating a user.
 * Back-end REST-API   (POST /api/users | PUT /api/users/:id)
 * ожидает следующий JSON-payload:
 * {
 *    email, password,
 *    lastName, firstName, middleName,
 *    role, status,
 *    departmentId   (numeric)
 * }
 */
const UsersEditor = ({ user, onSave, onCancel }) => {
  /* ---------------- Состояние формы ---------------- */
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    lastName: user?.lastName || '',
    firstName: user?.firstName || '',
    middleName: user?.middleName || '',
    role: user?.id ? user?.role || 'user' : '',
    status: user?.id ? user?.status || 'active' : '',
    departmentId: user?.departmentId || ''        // ID подразделения
  });

  const [departments, setDepartments] = useState([]);
  const [showPassword, setShowPassword] = useState(false);

  /* --------------- Получаем список подразделений ---------------- */
  useEffect(() => {
    (async () => {
      try {
        const data = await getDepartments(); // [{id,name,fullname},…]
        setDepartments(data);
      } catch (e) {
        console.error('Error fetching departments:', e);
      }
    })();
  }, []);

  /* --------------- При редактировании… ---------------- */
  useEffect(() => {
    if (user?.id) {
      setFormData({
        email: user.email || '',
        password: '',
        lastName: user.lastName || '',
        firstName: user.firstName || '',
        middleName: user.middleName || '',
        role: user.role || 'user',
        status: user.status || 'active',
        departmentId: user.departmentId || ''
      });
    }
  }, [user]);

  /* --------------- Обработчики ---------------- */
  const handleChange = ({ target: { name, value } }) =>
    setFormData(prev => ({ ...prev, [name]: value }));

  const handleSubmit = async (e) => {
    e.preventDefault();

    const { email, lastName, firstName, departmentId } = formData;
    if (!email || !lastName || !firstName || !departmentId) {
      alert('Заполните обязательные поля: Email, Фамилия, Имя, Подразделение');
      return;
    }

    // формируем payload
    const payload = { ...formData };
    if (user?.id && !payload.password) delete payload.password;

    console.log('[UsersEditor] → payload:', payload);

    try {
      if (user?.id) {
        await updateUser(user.id, payload);
      } else {
        await createUser(payload);
      }
      onSave(); // закрываем модальное окно только при успехе
    } catch (error) {
      console.error('Error saving user:', error);
      const msg = 
        error.response?.data?.message ||
        error.response?.data ||
        error.message ||
        'Неизвестная ошибка';
      alert(`Ошибка при сохранении: ${msg}`);
    }
  };

  /* --------------- UI helpers ---------------- */
  const togglePasswordVisibility = () => setShowPassword(!showPassword);

  // Доступные роли
  const roles = [
    { value: 'admin', label: 'Администратор' },
    { value: 'user', label: 'Пользователь' },
    { value: 'hr', label: 'Кадровый сотрудник' }
  ];

  // Доступные статусы
  const statuses = [
    { value: 'active', label: 'Активный' },
    { value: 'blocked', label: 'Заблокирован' }
  ];

  return (
    <div className="editor-modal">
      <form onSubmit={handleSubmit} className="editor-form">
        <button 
          type="button" 
          className="close-button" 
          onClick={onCancel} 
          aria-label="Закрыть"
        ></button>

        <h2>{user?.id ? 'Пользователь (редактирование)' : 'Пользователь (создание)'}</h2>

        <div className="name-row">
          <label>
            Фамилия:
            <input
              type="text"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Имя:
            <input
              type="text"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Отчество:
            <input
              type="text"
              name="middleName"
              value={formData.middleName}
              onChange={handleChange}
            />
          </label>
        </div>

        <div className="form-groups-container">
          <div className="form-group form-group-department">
            <label>
              Подразделение:
              <select
                name="departmentId"
                value={formData.departmentId}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите подразделение</option>
                {departments.map(dept => (
                  <option key={dept.id} value={dept.id}>
                    {dept.name} ({dept.fullname})
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-email">
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </label>
          </div>

          <div className="form-group form-group-password">
            <label>
              Пароль:
              <div className="password-input-container">
                <input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required={!user?.id}
                  placeholder={user?.id ? "Оставьте пустым, чтобы не менять" : ""}
                />
                <button 
                  type="button" 
                  className="password-toggle" 
                  onClick={togglePasswordVisibility}
                >
                  <i className={`password-icon ${showPassword ? 'visible' : 'hidden'}`}></i>
                </button>
              </div>
            </label>
          </div>

          <div className="form-group form-group-role">
            <label>
              Роль:
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите роль</option>
                {roles.map(role => (
                  <option key={role.value} value={role.value}>
                    {role.label}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-status">
            <label>
              Статус:
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите статус</option>
                {statuses.map(status => (
                  <option key={status.value} value={status.value}>
                    {status.label}
                  </option>
                ))}
              </select>
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" className="btn-save">
            Сохранить
          </button>
          <button 
            type="button" 
            onClick={onCancel}
            className="btn-cancel"
          >
            Отмена
          </button>
        </div>
      </form>
    </div>
  );
};

export default UsersEditor;
