import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Navbar from '../components/Navbar';
import './News.css';

const API_BASE = process.env.REACT_APP_API_URL || 'http://localhost:5000';
const PER_PAGE = 5;

const News = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);

  useEffect(() => {
    const fetchNews = async () => {
      setLoading(true);
      try {
        const { data } = await axios.get(`${API_BASE}/api/news`, {
          params: { page: currentPage, limit: PER_PAGE },
        });

        if (data.news && data.news.length > 0) {
          setNews(data.news);
          setTotalPages(data.totalPages);
          setError(null);
        } else {
          setError('Новостей пока нет.');
        }
      } catch (err) {
        console.error('Error fetching news:', err);
        setError('Не удалось загрузить новости');
      } finally {
        setLoading(false);
      }
    };

    fetchNews();
  }, [currentPage]);

  const goPrev = () => setCurrentPage((p) => Math.max(1, p - 1));
  const goNext = () => setCurrentPage((p) => Math.min(totalPages, p + 1));

  if (loading) {
    return (
      <div className="app">
        <Navbar />
        <div className="main-content">
          <div className="loading">Загрузка новостей...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <Navbar />
        <div className="main-content">
          <div className="error">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <Navbar />
      <div className="main-content">
        <div className="news-container">
          <section className="hero">
            <h1>Новости предприятия</h1>
            <p>Актуальные события и объявления</p>
          </section>
          <div className="news-list">
            {news.map(item => (
              <div key={item.id} className="news-item">
                <h2>{item.title}</h2>
                <p className="news-content">{item.content}</p>
                <div className="news-meta">
                  <span className="news-date">{item.date || item.createdAt?.slice(0,10)}</span>
                  <span className="news-author">{item.author || 'Администратор'}</span>
                </div>
              </div>
            ))}
          </div>

          {/* Pagination controls */}
          {totalPages > 1 && (
            <div className="pagination">
              <button onClick={goPrev} disabled={currentPage === 1}>
                ‹ Пред
              </button>
              <span>
                {currentPage} / {totalPages}
              </span>
              <button onClick={goNext} disabled={currentPage === totalPages}>
                След ›
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default News;
