import React, { useState, useEffect } from 'react';
import { createUser, updateUser } from '../api/usersApi';
import { getDepartments } from '../api/departmentsApi';
import './UsersEditor.css';

const UsersEditor = ({ user, onSave, onCancel }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    lastname: user?.lastname || '',
    firstname: user?.firstname || '',
    midlename: user?.midlename || '',
    role: user?.id ? user?.role || 'user' : '',
    status: user?.id ? user?.status || 'active' : '',
    department: user?.department || ''
  });

  const [departments, setDepartments] = useState([]);
  const [setLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);

  // Получаем список отделов при загрузке компонента
  useEffect(() => {
    const fetchDepartments = async () => {
      setLoading(true);
      try {
        const data = await getDepartments();
        setDepartments(data);
      } catch (error) {
        console.error('Error fetching departments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDepartments();
  }, []);

  // Если у нас есть ID пользователя, загружаем его данные в форму
  useEffect(() => {
    if (user?.id) {
      setFormData({
        email: user.email || '',
        password: '',
        lastname: user.lastname || '',
        firstname: user.firstname || '',
        midlename: user.midlename || '',
        role: user.role || 'user',
        status: user.status || 'active',
        department: user.department || ''
      });
    }
  }, [user]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Вывод данных формы в терминал
    console.log('Данные формы перед отправкой:', JSON.stringify(formData, null, 2));

    // Проверка обязательных полей
    if (!formData.email || !formData.lastname || !formData.firstname || !formData.department) {
      alert('Заполните все обязательные поля: Email, Фамилия, Имя, Подразделение');
      return;
    }

    // формируем payload
    const payload = { ...formData };
    if (user?.id && !payload.password) delete payload.password;

    try {
      if (user?.id) {
        await updateUser(user.id, payload);
      } else {
        await createUser(payload);
      }
      onSave();
    } catch (error) {
      console.error('Ошибка сохранения:', error);
      const msg =
        error.response?.data?.message ||
        error.response?.data ||
        error.message ||
        'Неизвестная ошибка';
      alert(`Ошибка при сохранении: ${msg}`);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  // Доступные роли
  const roles = [
    { value: 'admin', label: 'Администратор' },
    { value: 'user', label: 'Пользователь' },
    { value: 'hr', label: 'Кадровый сотрудник' }
  ];

  // Доступные статусы
  const statuses = [
    { value: 'active', label: 'Активный' },
    { value: 'blocked', label: 'Заблокирован' }
  ];

  return (
    <div className="editor-modal">
      <form onSubmit={handleSubmit} className="editor-form">
        <button 
          type="button" 
          className="close-button" 
          onClick={onCancel} 
          aria-label="Закрыть"
        ></button>

        <h2>{user?.id ? 'Пользователь (редактирование)' : 'Пользователь (создание)'}</h2>

        <div className="name-row">
          <label>
            Фамилия:
            <input
              type="text"
              name="lastname"
              value={formData.lastname}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Имя:
            <input
              type="text"
              name="firstname"
              value={formData.firstname}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            Отчество:
            <input
              type="text"
              name="midlename"
              value={formData.midlename}
              onChange={handleChange}
            />
          </label>
        </div>

        <div className="form-groups-container">
          <div className="form-group form-group-department">
            <label>
              Подразделение:
              <select
                name="department"
                value={formData.department}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите подразделение</option>
                {departments.map(dept => (
                  <option key={dept.id} value={dept.name}>
                    {dept.name} ({dept.fullname})
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-email">
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </label>
          </div>

          <div className="form-group form-group-password">
            <label>
              Пароль:
              <div className="password-input-container">
                <input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required={!user?.id}
                  placeholder={user?.id ? "Оставьте пустым, чтобы не менять" : ""}
                />
                <button 
                  type="button" 
                  className="password-toggle" 
                  onClick={togglePasswordVisibility}
                >
                  <i className={`password-icon ${showPassword ? 'visible' : 'hidden'}`}></i>
                </button>
              </div>
            </label>
          </div>

          <div className="form-group form-group-role">
            <label>
              Роль:
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите роль</option>
                {roles.map(role => (
                  <option key={role.value} value={role.value}>
                    {role.label}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-status">
            <label>
              Статус:
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                required
                className="select-placeholder"
              >
                <option value="" disabled>Выберите статус</option>
                {statuses.map(status => (
                  <option key={status.value} value={status.value}>
                    {status.label}
                  </option>
                ))}
              </select>
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" className="btn-save">
            Сохранить
          </button>
          <button 
            type="button" 
            onClick={onCancel}
            className="btn-cancel"
          >
            Отмена
          </button>
        </div>
      </form>
    </div>
  );
};

export default UsersEditor;
