import React, { useState, useEffect } from 'react';
import { getNews } from '../api/newsApi';
import Navbar from '../components/Navbar';
import NewsCard from '../components/NewsCard';
import styles from '../styles/newsStyles.module.css';
import './News.css';

const News = () => {
  const [news, setNews] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5); // Количество новостей на странице

  useEffect(() => {
    const fetchNews = async () => {
      try {
        const data = await getNews();
        if (data && data.length > 0) {
          setNews(data);
        } else {
          setError('Новостей пока нет');
        }
      } catch (err) {
        console.error('Error fetching news:', err);
        setError('Не удалось загрузить новости');
      } finally {
        setLoading(false);
      }
    };
    fetchNews();
  }, []);

  // Пагинация
  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentNews = news.slice(indexOfFirstItem, indexOfLastItem);
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (loading) {
    return (
      <div className="app">
        <Navbar />
        <div className="main-content">
          <div className="loading">Загрузка новостей...</div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="app">
        <Navbar />
        <div className="main-content">
          <div className="error">{error}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="app">
      <Navbar />
      <div className="main-content">
        <div className="news-container">
          <section className="hero">
            <h1>Новости предприятия</h1>
            <p>Актуальные события и объявления</p>
          </section>
          {loading ? (
            <div className="loading">Загрузка новостей...</div>
          ) : error ? (
            <div className="error">{error}</div>
          ) : (
            <>
              <div className={styles.newsGrid}>
                {currentNews.map(item => (
                  <NewsCard key={item.id} newsItem={item} showFullContent={true} />
                ))}
              </div>
              {/* Пагинация */}
              <div className="pagination">
                {Array.from({ length: Math.ceil(news.length / itemsPerPage) }, (_, i) => (
                  <button
                    key={i + 1}
                    onClick={() => paginate(i + 1)}
                    className={currentPage === i + 1 ? 'active' : ''}
                  >
                    {i + 1}
                  </button>
                ))}
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default News;
