import React, { useState, useEffect } from 'react';
import { getNews, deleteNews, publishNews } from '../api/newsApi';
import { getDepartments, deleteDepartment } from '../api/departmentsApi';
import NewsEditor from '../components/NewsEditor';
import DepartmentEditor from '../components/DepartmentEditor';
import { useAuth } from '../context/AuthContext';
import './AdminPanel.css';

const AdminPanel = () => {
  const [news, setNews] = useState([]);
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingNews, setEditingNews] = useState(null);
  const [editingDept, setEditingDept] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);
  const [activeTab, setActiveTab] = useState('news');
  const { user, isAdmin } = useAuth();

  const loadNews = async () => {
    try {
      const data = await getNews();
      setNews(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  const loadDepartments = async () => {
    try {
      const data = await getDepartments();
      setDepartments(data);
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    if (isAdmin()) {
      loadNews();
      loadDepartments();
    }
  }, [isAdmin]);

  // Обработчики для новостей
  const handleDeleteNews = async (id) => {
    if (!isAdmin()) return;
    try {
      await deleteNews(id);
      setNews(prev => prev.filter(item => item.id !== id));
    } catch (err) {
      setError(err.message);
    }
  };

  const handlePublishNews = async (id) => {
    if (!isAdmin()) return;
    try {
      await publishNews(id);
      setNews(prev => prev.map(item => 
        item.id === id ? { ...item, published: !item.published } : item
      ));
    } catch (err) {
      setError(err.message);
    }
  };

  // Обработчики для подразделений
  const handleDeleteDept = async (id) => {
    if (!isAdmin()) return;
    try {
      await deleteDepartment(id);
      setDepartments(prev => prev.filter(dept => dept.id !== id));
    } catch (err) {
      setError(err.message);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentNews = news.slice(indexOfFirstItem, indexOfLastItem);
  const currentDepts = departments.slice(indexOfFirstItem, indexOfLastItem);

  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  if (!isAdmin()) return <div className="error">Доступ запрещен</div>;
  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">Ошибка: {error}</div>;

  return (
    <div className="admin-container">
      <h1>Панель администратора</h1>

      <div className="tabs">
        <button
          className={`tab ${activeTab === 'news' ? 'active' : ''}`}
          onClick={() => setActiveTab('news')}
        >
          Управление новостями
        </button>
        <button
          className={`tab ${activeTab === 'departments' ? 'active' : ''}`}
          onClick={() => setActiveTab('departments')}
        >
          Управление структурой
        </button>
      </div>

      {activeTab === 'news' && (
        <>
          <button 
            onClick={() => setEditingNews({ title: '', content: '' })}
            className="btn-add"
          >
            + Добавить новость
          </button>

          {editingNews && (
            <NewsEditor
              news={editingNews}
              onSave={() => {
                setEditingNews(null);
                loadNews();
              }}
              onCancel={() => setEditingNews(null)}
            />
          )}

          <table className="news-table">
            <thead>
              <tr>
                <th>Заголовок</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {currentNews.map(item => (
                <tr key={item.id}>
                  <td>{item.title}</td>
                  <td>
                    <button 
                      onClick={() => setEditingNews(item)}
                      className="btn-edit"
                    >
                      Редактировать
                    </button>
                    <button
                      onClick={() => handlePublishNews(item.id)}
                      className={`btn-publish ${item.published ? 'published' : ''}`}
                    >
                      {item.published ? 'Снять с публикации' : 'Опубликовать'}
                    </button>
                    <button
                      onClick={() => handleDeleteNews(item.id)}
                      className="btn-delete"
                    >
                      Удалить
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            {Array.from({ length: Math.ceil(news.length / itemsPerPage) }, (_, i) => (
              <button key={i + 1} onClick={() => paginate(i + 1)}>
                {i + 1}
              </button>
            ))}
          </div>
        </>
      )}

      {activeTab === 'departments' && (
        <>
          <button 
            onClick={() => setEditingDept({ 
              name: '', 
              fullname: '', 
              code_zup: '', 
              organization: '' 
            })}
            className="btn-add"
          >
            + Добавить подразделение
          </button>

          {editingDept && (
            <DepartmentEditor
              department={editingDept}
              onSave={() => {
                setEditingDept(null);
                loadDepartments();
              }}
              onCancel={() => setEditingDept(null)}
            />
          )}

          <table className="departments-table">
            <thead>
              <tr>
                <th>Название</th>
                <th>Полное название</th>
                <th>Код ЗУП</th>
                <th>Действия</th>
              </tr>
            </thead>
            <tbody>
              {currentDepts.map(dept => (
                <tr key={dept.id}>
                  <td>{dept.name}</td>
                  <td>{dept.fullname}</td>
                  <td>{dept.code_zup}</td>
                  <td>
                    <button 
                      onClick={() => setEditingDept(dept)}
                      className="btn-edit"
                    >
                      Редактировать
                    </button>
                    <button
                      onClick={() => handleDeleteDept(dept.id)}
                      className="btn-delete"
                    >
                      Удалить
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <div className="pagination">
            {Array.from({ length: Math.ceil(departments.length / itemsPerPage) }, (_, i) => (
              <button key={i + 1} onClick={() => paginate(i + 1)}>
                {i + 1}
              </button>
            ))}
          </div>
        </>
      )}
    </div>
  );
};

export default AdminPanel;
