import React, { useState, useEffect } from 'react';
import { getDepartments, deleteDepartment } from '../../api/departmentsApi';
import DepartmentEditor from '../DepartmentEditor';
import Pagination from '../common/Pagination';

const DepartmentsTab = () => {
  const [departments, setDepartments] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [editingDept, setEditingDept] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage] = useState(5);

  const loadDepartments = async () => {
    try {
      const data = await getDepartments();
      setDepartments(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadDepartments();
  }, []);

  const handleDeleteDept = async (id) => {
    try {
      await deleteDepartment(id);
      setDepartments(prev => prev.filter(dept => dept.id !== id));
    } catch (err) {
      setError(err.message);
    }
  };

  const indexOfLastItem = currentPage * itemsPerPage;
  const indexOfFirstItem = indexOfLastItem - itemsPerPage;
  const currentItems = departments.slice(indexOfFirstItem, indexOfLastItem);

  if (loading) return <div className="loading">Загрузка...</div>;
  if (error) return <div className="error">Ошибка: {error}</div>;

  return (
    <>
      <button 
        onClick={() => setEditingDept({ 
          name: '', 
          fullname: '', 
          code_zup: '', 
          organization: '' 
        })}
        className="btn-add"
      >
        + Добавить подразделение
      </button>

      {editingDept && (
        <DepartmentEditor
          department={editingDept}
          onSave={() => {
            setEditingDept(null);
            loadDepartments();
          }}
          onCancel={() => setEditingDept(null)}
        />
      )}

      <table className="departments-table">
        <thead>
          <tr>
            <th>Наименование</th>
            <th>Полное наименование</th>
            <th>Код ЗУП</th>
            <th>Организация</th>
            <th>Действия</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map(dept => (
            <tr key={dept.id}>
              <td>{dept.name}</td>
              <td>{dept.fullname}</td>
              <td>{dept.code_zup}</td>
              <td>{dept.organization}</td>
              <td>
                <button 
                  onClick={() => setEditingDept(dept)}
                  className="btn-edit"
                >
                  Редактировать
                </button>
                <button
                  onClick={() => handleDeleteDept(dept.id)}
                  className="btn-delete"
                >
                  Удалить
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      <Pagination
        itemsCount={departments.length}
        itemsPerPage={itemsPerPage}
        currentPage={currentPage}
        onPageChange={setCurrentPage}
      />
    </>
  );
};

export default DepartmentsTab;
