import React, { useState } from 'react';
import './AdminPanel.css';

const AdminPanel = () => {
  // исходный список пользователей
  const [users, setUsers] = useState([
    { id: 1, name: 'Администратор', email: 'admin@nsrz.ru', role: 'admin' },
    { id: 2, name: 'Сотрудник',    email: 'user@nsrz.ru', role: 'employee' }
  ]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [showModal, setShowModal]       = useState(false);

  // Создать нового пользователя (пустая форма)
  const handleCreateUser = () => {
    const newUser = { id: users.length + 1, name: '', email: '', role: 'employee' };
    setUsers([newUser, ...users]);
    setSelectedUser(newUser);
    setShowModal(true);
  };

  // Копировать выбранного пользователя
  const handleCopyUser = () => {
    if (!selectedUser) return;
    const copy = {
      ...selectedUser,
      id: users.length + 1,
      name: selectedUser.name + ' (копия)'
    };
    setUsers([...users, copy]);
  };

  // Удалить выбранного пользователя
  const handleDeleteUser = () => {
    if (!selectedUser) return;
    setUsers(users.filter(u => u.id !== selectedUser.id));
    setSelectedUser(null);
  };

  // Открыть карточку по клику на строку
  const handleRowClick = user => {
    setSelectedUser(user);
    setShowModal(true);
  };

  return (
    <div className="admin-panel">
      <section className="hero">
        <h1>Панель администратора</h1>
        <p>Управление пользователями и контентом</p>
      </section>

      <div className="section-header">
        <h2>Пользователи</h2>
        <div className="admin-buttons">
          <button className="btn create-button" onClick={handleCreateUser}>
            Создать
          </button>
          <button className="btn copy-button" onClick={handleCopyUser} disabled={!selectedUser}>
            Копировать
          </button>
          <button className="btn delete-button" onClick={handleDeleteUser} disabled={!selectedUser}>
            Удалить
          </button>
        </div>
      </div>

      <div className="table-wrapper">
        <table className="users-table">
          <thead>
            <tr>
              <th>Имя пользователя</th>
              <th>Адрес электронной почты</th>
              <th>Роль</th>
            </tr>
          </thead>
          <tbody>
            {users.map(user => (
              <tr
                key={user.id}
                className={selectedUser?.id === user.id ? 'active-row' : ''}
                onClick={() => handleRowClick(user)}
              >
                <td>{user.name}</td>
                <td>{user.email}</td>
                <td>{user.role === 'admin' ? 'Администратор' : 'Сотрудник'}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && selectedUser && (
        <div className="modal-overlay" onClick={() => setShowModal(false)}>
          <div className="user-modal" onClick={e => e.stopPropagation()}>
            <button className="close-button" onClick={() => setShowModal(false)}>×</button>
            <h2>Карточка пользователя</h2>
            <div className="modal-content">
              <p><strong>Имя:</strong> {selectedUser.name}</p>
              <p><strong>Email:</strong> {selectedUser.email}</p>
              <p><strong>Роль:</strong> {selectedUser.role === 'admin' ? 'Администратор' : 'Сотрудник'}</p>
              {/* сюда можно добавить форму редактирования */}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
