import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [publishedNews, setPublishedNews] = useState([]);

  useEffect(() => {
    // Загрузка только опубликованных новостей
    const fetchPublishedNews = async () => {
      try {
        const response = await axios.get('/api/news?published=true');
        setPublishedNews(response.data);
      } catch (error) {
        console.error('Ошибка при загрузке новостей:', error);
      }
    };

    fetchPublishedNews();
  }, []);

  return (
    <div className="home-page">
      <section className="hero">
        <h1>Новости компании</h1>
        <p>Актуальные события и объявления</p>
      </section>

      <div className="news-section">
        <h2>Последние новости</h2>
        {publishedNews.length > 0 ? (
          <div className="news-list">
            {publishedNews.map(newsItem => (
              <div key={newsItem.id} className="news-card">
                <h3>{newsItem.title}</h3>
                <div 
                  className="news-content" 
                  dangerouslySetInnerHTML={{ __html: newsItem.content }} 
                />
                <div className="news-meta">
                  <span>{new Date(newsItem.date).toLocaleDateString()}</span>
                  <span>{newsItem.author}</span>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <p className="no-news">Нет опубликованных новостей</p>
        )}
      </div>
    </div>
  );
};

export default Home;
