  useEffect(() => {
    const fetchDepartments = async () => {
      setLoading(true);
      try {
        const data = await getDepartments();
        setDepartments(data);
      } catch (error) {
        console.error('Error fetching departments:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchDepartments();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (user?.id) {
        await updateUser(user.id, formData);
      } else {
        await createUser(formData);
      }
      onSave();
    } catch (error) {
      console.error('Error saving user:', error);
    }
  };

  const togglePasswordVisibility = () => {
    setShowPassword(!showPassword);
  };

  // –î–æ—Å—Ç—É–ø–Ω—ã–µ —Ä–æ–ª–∏
  const roles = [
    { value: 'admin', label: '–ê–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä' },
    { value: 'user', label: '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å' },
    { value: 'hr', label: '–ö–∞–¥—Ä–æ–≤—ã–π —Å–æ—Ç—Ä—É–¥–Ω–∏–∫' }
  ];

  // –î–æ—Å—Ç—É–ø–Ω—ã–µ —Å—Ç–∞—Ç—É—Å—ã
  const statuses = [
    { value: 'active', label: '–ê–∫—Ç–∏–≤–Ω—ã–π' },
    { value: 'blocked', label: '–ó–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞–Ω' }
