    <div className="editor-modal">
      <form onSubmit={handleSubmit} className="editor-form">
        <h2>{user?.id ? '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å (—Ä–µ–¥–∞–∫—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ)' : '–ü–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å (—Å–æ–∑–¥–∞–Ω–∏–µ)'}</h2>

        <div className="name-row">
          <label>
            –§–∞–º–∏–ª–∏—è:
            <input
              type="text"
              name="lastname"
              value={formData.lastname}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            –ò–º—è:
            <input
              type="text"
              name="firstname"
              value={formData.firstname}
              onChange={handleChange}
              required
            />
          </label>

          <label>
            –û—Ç—á–µ—Å—Ç–≤–æ:
            <input
              type="text"
              name="midlename"
              value={formData.midlename}
              onChange={handleChange}
            />
          </label>
        </div>

        <div className="form-groups-container">
          <div className="form-group form-group-department">
            <label>
              –ü–æ–¥—Ä–∞–∑–¥–µ–ª–µ–Ω–∏–µ:
              <select
                name="department"
                value={formData.department}
                onChange={handleChange}
                required
                className="placeholder-text"
              >
                <option value="" disabled>–í—ã–±–µ—Ä–∏—Ç–µ –ø–æ–¥—Ä–∞–∑–¥–µ–ª–µ–Ω–∏–µ</option>
                {departments.map(dept => (
                  <option key={dept.id} value={dept.name}>
                    {dept.name} ({dept.fullname})
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-email">
            <label>
              Email:
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </label>
          </div>

          <div className="form-group form-group-password">
            <label>
              –ü–∞—Ä–æ–ª—å:
              <div className="password-container">
                <input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  value={formData.password}
                  onChange={handleChange}
                  required={!user?.id}
                  placeholder={user?.id ? "–û—Å—Ç–∞–≤—å—Ç–µ –ø—É—Å—Ç—ã–º, —á—Ç–æ–±—ã –Ω–µ –º–µ–Ω—è—Ç—å" : ""}
                />
                <button 
                  type="button" 
                  className="password-toggle" 
                  onClick={togglePasswordVisibility}
                >
                  {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
                </button>
              </div>
            </label>
          </div>

          <div className="form-group form-group-role">
            <label>
              –†–æ–ª—å:
              <select
                name="role"
                value={formData.role}
                onChange={handleChange}
                required
                className="placeholder-text"
              >
                <option value="" disabled>–í—ã–±–µ—Ä–∏—Ç–µ —Ä–æ–ª—å</option>
                {roles.map(role => (
                  <option key={role.value} value={role.value}>
                    {role.label}
                  </option>
                ))}
              </select>
            </label>
          </div>

          <div className="form-group form-group-status">
            <label>
              –°—Ç–∞—Ç—É—Å:
              <select
                name="status"
                value={formData.status}
                onChange={handleChange}
                required
                className="placeholder-text"
              >
                <option value="" disabled>–í—ã–±–µ—Ä–∏—Ç–µ —Å—Ç–∞—Ç—É—Å</option>
                {statuses.map(status => (
                  <option key={status.value} value={status.value}>
                    {status.label}
                  </option>
                ))}
              </select>
            </label>
          </div>
        </div>

        <div className="form-actions">
          <button type="submit" className="editor-btn btn-save">
            –°–æ—Ö—Ä–∞–Ω–∏—Ç—å
          </button>
          <button 
