import React, { useState } from 'react';
import './Phonebook.css';

const Phonebook = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('all');

  // Временные данные
  const departments = [
    { id: 'all', name: 'Все отделы' },
    { id: 'management', name: 'Руководство' },
    { id: 'production', name: 'Производственный отдел' },
    { id: 'hr', name: 'Отдел кадров' }
  ];

  const employees = [
    { 
      id: 1, 
      name: 'Иванов Иван', 
      department: 'management', 
      departmentName: 'Руководство',
      position: 'Директор', 
      phone: '123-456',
      email: 'ivanov@nsrz.ru'
    },
    { 
      id: 2, 
      name: 'Петров Петр', 
      department: 'production', 
      departmentName: 'Производственный отдел',
      position: 'Начальник цеха', 
      phone: '234-567',
      email: 'petrov@nsrz.ru'
    },
    { 
      id: 3, 
      name: 'Сидорова Анна', 
      department: 'hr', 
      departmentName: 'Отдел кадров',
      position: 'HR-менеджер', 
      phone: '345-678',
      email: 'sidorova@nsrz.ru'
    }
  ];

  const filteredEmployees = employees.filter(employee => {
    const matchesSearch = employee.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = selectedDepartment === 'all' || employee.department === selectedDepartment;
    return matchesSearch && matchesDepartment;
  });

  return (
    <div className="phonebook-container">
      <section className="hero">
        <h1>Справочник телефонов</h1>
        <p>Контактная информация сотрудников</p>
      </section>

      <div className="filters">
        <input
          type="text"
          placeholder="Поиск по имени..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />

        <select
          value={selectedDepartment}
          onChange={(e) => setSelectedDepartment(e.target.value)}
          className="department-select"
        >
          {departments.map(dept => (
            <option key={dept.id} value={dept.id}>
              {dept.name}
            </option>
          ))}
        </select>
      </div>

      <div className="table-container">
        <table className="employees-table">
          <thead>
            <tr>
              <th>Сотрудник</th>
              <th>Должность</th>
              <th>Подразделение</th>
              <th>Телефон</th>
              <th>Электронная почта</th>
            </tr>
          </thead>
          <tbody>
            {filteredEmployees.map(employee => (
              <tr key={employee.id}>
                <td>{employee.name}</td>
                <td>{employee.position}</td>
                <td>{employee.departmentName}</td>
                <td>{employee.phone}</td>
                <td>{employee.email}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Phonebook;
