import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './Phonebook.css';

const Phonebook = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedDepartment, setSelectedDepartment] = useState('all');

  // –í—Ä–µ–º–µ–Ω–Ω—ã–µ –¥–∞–Ω–Ω—ã–µ
  const departments = [
    { id: 'all', name: '–í—Å–µ –æ—Ç–¥–µ–ª—ã' },
    { id: 'management', name: '–†—É–∫–æ–≤–æ–¥—Å—Ç–≤–æ' },
    { id: 'production', name: '–ü—Ä–æ–∏–∑–≤–æ–¥—Å—Ç–≤–µ–Ω–Ω—ã–π –æ—Ç–¥–µ–ª' },
    { id: 'hr', name: '–û—Ç–¥–µ–ª –∫–∞–¥—Ä–æ–≤' }
  ];

  const contacts = [
    { id: 1, name: '–ò–≤–∞–Ω–æ–≤ –ò–≤–∞–Ω', department: 'management', position: '–î–∏—Ä–µ–∫—Ç–æ—Ä', phone: '123-456' },
    { id: 2, name: '–ü–µ—Ç—Ä–æ–≤ –ü–µ—Ç—Ä', department: 'production', position: '–ù–∞—á–∞–ª—å–Ω–∏–∫ —Ü–µ—Ö–∞', phone: '234-567' },
    { id: 3, name: '–°–∏–¥–æ—Ä–æ–≤–∞ –ê–Ω–Ω–∞', department: 'hr', position: 'HR-–º–µ–Ω–µ–¥–∂–µ—Ä', phone: '345-678' }
  ];

  const filteredContacts = contacts.filter(contact => {
    const matchesSearch = contact.name.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesDepartment = selectedDepartment === 'all' || contact.department === selectedDepartment;
    return matchesSearch && matchesDepartment;
  });

  return (
    <div className="app">
      <Navbar />
      <div className="main-content">
        <div className="phonebook-container">
          <section className="hero">
            <h1>–°–ø—Ä–∞–≤–æ—á–Ω–∏–∫ —Ç–µ–ª–µ—Ñ–æ–Ω–æ–≤</h1>
            <p>–ö–æ–Ω—Ç–∞–∫—Ç–Ω–∞—è –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è —Å–æ—Ç—Ä—É–¥–Ω–∏–∫–æ–≤</p>
          </section>

          <div className="filters">
            <input
              type="text"
              placeholder="–ü–æ–∏—Å–∫ –ø–æ –∏–º–µ–Ω–∏..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />

            <select
              value={selectedDepartment}
              onChange={(e) => setSelectedDepartment(e.target.value)}
              className="department-select"
            >
              {departments.map(dept => (
                <option key={dept.id} value={dept.id}>
                  {dept.name}
                </option>
              ))}
            </select>
          </div>

          <div className="contacts-list">
            {filteredContacts.map(contact => (
              <div key={contact.id} className="contact-card">
                <h3>{contact.name}</h3>
                <p className="position">{contact.position}</p>
                <p className="phone">üìû {contact.phone}</p>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Phonebook;
