import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import './Home.css';

const Home = () => {
  const [latestNews, setLatestNews] = useState([]);

  useEffect(() => {
    const fetchPublishedNews = async () => {
      try {
        const response = await axios.get('/api/news/latest');
        setLatestNews(response.data);
      } catch (error) {
        console.error('Ошибка загрузки новостей:', error);
      }
    };

    fetchPublishedNews();
  }, []);

  return (
    <div className="home-page">
      <section className="hero">
        <h1>Новости компании</h1>
        <p>Актуальные события и обновления</p>
      </section>

      <section className="latest-news">
        <h2>Последние новости</h2>
        {latestNews.length > 0 ? (
          <div className="news-grid">
            {latestNews.slice(0, 3).map(news => (
              <div key={news.id} className="news-card">
                <h3>{news.title}</h3>
                <div 
                  className="news-preview" 
                  dangerouslySetInnerHTML={{ __html: news.content.substring(0, 150) + '...' }} 
                />
                <Link to={`/news/${news.id}`} className="read-more">Читать далее</Link>
              </div>
            ))}
          </div>
        ) : (
          <p>Нет опубликованных новостей</p>
        )}
        <Link to="/news" className="all-news-link">Все новости →</Link>
      </section>
    </div>
  );
};

export default Home;
